/** Global definitions for developement **/

// for style loader
declare module "*.css" {
  const styles: any;
  export = styles;
}

declare module "*.scss" {
  const styles: any;
  export = styles;
}

declare module "*.png" {
  const o: any;
  export = o;
}
declare module "*.jpg" {
  const o: any;
  export = o;
}
declare module "*.svg" {
  const o: any;
  export = o;
}


